const fs = require('fs');
const path = require('path');

// Path to our data file
const DATA_FILE = path.join('/tmp', 'items-data.json');

// Define default items directly in the function instead of importing
const defaultItems = [
  {
    id: "1",
    name: "Vintage Camera",
    imageUrl: "data:image/png;base64,",
    details: "A vintage film camera from the 1970s in excellent condition.",
    status: "not_sold",
    dateAdded: new Date().toISOString()
  },
  {
    id: "2",
    name: "Antique Chair",
    imageUrl: "data:image/png;base64,",
    details: "Hand-carved wooden chair from the early 1900s.",
    status: "sold",
    dateAdded: new Date().toISOString()
  }
];

exports.handler = async (event) => {
  // Set CORS headers
  const headers = {
    'Access-Control-Allow-Origin': '*',
    'Access-Control-Allow-Headers': 'Content-Type',
    'Access-Control-Allow-Methods': 'GET, OPTIONS',
    'Content-Type': 'application/json'
  };

  // Handle preflight OPTIONS request
  if (event.httpMethod === 'OPTIONS') {
    return { statusCode: 200, headers, body: '' };
  }

  // Only handle GET requests
  if (event.httpMethod !== 'GET') {
    return { 
      statusCode: 405, 
      headers, 
      body: JSON.stringify({ error: 'Method not allowed' }) 
    };
  }

  try {
    // Check if the file exists
    if (fs.existsSync(DATA_FILE)) {
      // Read the items from the file
      const data = fs.readFileSync(DATA_FILE, 'utf8');
      return {
        statusCode: 200,
        headers,
        body: data
      };
    } else {
      // Return default items if file doesn't exist
      return {
        statusCode: 200,
        headers,
        body: JSON.stringify(defaultItems)
      };
    }
  } catch (error) {
    console.error('Error getting items:', error);
    return {
      statusCode: 500,
      headers,
      body: JSON.stringify({ error: 'Failed to get items' })
    };
  }
};